import React, { useState } from "react";
import "./Teams.css";

function PersonButton({
    name,
    selected,
    onClick,
  }: {
    name: String;
    selected: boolean;
    onClick: () => void;
}){
    return (
        <button
          className={`person ${selected ? 'selected' : ''}`}
          onClick={onClick}
        >
          {name}
        </button>
    );
}

function TeamButton({
    number,
    selected,
    onTeamClick,
  }: {
    number: number;
    selected: boolean;
    onTeamClick: () => void;
  }) {

    const [person, setPerson] = useState<String>('');

    const onClick = (name: String) => {
        if(name === person) {
            setPerson('');
        } else {
            setPerson(name);
        }

    }

    return (
      <button
        className={`team-container ${selected ? 'selected' : ''}`}
        onClick={onTeamClick}
      >
        <div className="team-content">
          <div className="team-header">TEAM 1 TOTAL POINTS</div>
          <div className="points">
            <div className="current">1000</div>
            <div className="max">/ 9700</div>
          </div>
          <div className="members">
          <div className="members">
              {["Michael", "Kevin", "Bob", "Larissa", "Gabby"].map((name) => (
                <PersonButton
                  key={name}
                  name={name}
                  selected={person === name}
                  onClick={() => onClick(name)}
                />
              ))}
            </div>
          </div>
        </div>
      </button>
    );
  }

export default function Teams() {
    const [team, setTeam] = useState(0);

    const onTeamClick = (number: number) => {
        if(team === number) {
            setTeam(0);
        } else {
            setTeam(number);
        }

    }

  return (
    <div className="team-list">
      <TeamButton number={1} selected={team===1} onTeamClick={() => onTeamClick(1)}/>
      <TeamButton number={2} selected={team===1} onTeamClick={() => onTeamClick(2)}/>
      <TeamButton number={3} selected={team===1} onTeamClick={() => onTeamClick(3)}/>
    </div>
  );
}
