import axios from 'axios';

const API_URL = 'http://localhost:1337/task';

export const getTasks = async () => {
  try {
    const response = await axios.get(API_URL);
    return response.data;
  } catch (error) {
    console.error('Error fetching tasks:', error);
    throw error;
  }
};

export const getTasksForPerson = async (person: string) => {
  try {
    const PERSON_URL = API_URL + `/${person}`;
    console.log(PERSON_URL);
    const response = await axios.get(PERSON_URL, );
    return response.data;
  } catch (error) {
    console.error('Error fetching tasks:', error);
    throw error;
  }
};
