import React, { useState, useEffect } from "react";
import { getTasks } from "../services/taskService";
import { Task } from "../types";
import "./Tasks.css";

function TaskButton({
  number,
  selected,
  onClick,
}: {
  number: number;
  selected: boolean;
  onClick: () => void;
}) {
  return (
    <button className={`task ${selected ? "selected" : ""}`} onClick={onClick}>
      <div className="task-container">
        <div className="task-content">
          {number}
          <div className="info-button">
            <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24">
              <title>task-info</title>
              <path d="M11 7V9H13V7H11M14 17V15H13V11H10V13H11V15H10V17H14M22 12C22 17.5 17.5 22 12 22C6.5 22 2 17.5 2 12C2 6.5 6.5 2 12 2C17.5 2 22 6.5 22 12M20 12C20 7.58 16.42 4 12 4C7.58 4 4 7.58 4 12C4 16.42 7.58 20 12 20C16.42 20 20 16.42 20 12Z" />
            </svg>
          </div>
        </div>
      </div>
    </button>
  );
}

function Modal({ task, onClose }: { task: number; onClose: () => void }) {
  return (
    <div className="task-overlay" onClick={onClose}>
      <div
        className="task-overlay-content"
        onClick={(e) => e.stopPropagation()}
      >
        <p>Task Number: {task}</p>
      </div>
    </div>
  );
}

export default function Tasks() {
  const [tasks, setTasks] = useState<Task>();
  const [selectedTask, setSelectedTask] = useState(0);

  useEffect(() => {
    const fetchTasks = async () => {
      try {
        const data = await getTasks();

        setTasks(data);
      } catch (error) {
        console.error('Failed to fetch tasks:', error);
      }
    };

    fetchTasks();
  }, []);

  const onClick = (task: Task) => {
    if (task.task_id === selectedTask) {
      setSelectedTask(0);
    } else {
      setSelectedTask(task.task_id);
    }
  };

  const closeModal = () => {
    setSelectedTask(0);
  };

  return (
    <div className="task-list">

      {/* {tasks.map((task: Task, index: Number) => (
        <TaskButton
          key={task.task_id}
          number={index + 1}
          selected={selectedTask === task.task_id}
          onClick={() => onClick(task.task_id)}
        />
      ))} */}
      {selectedTask !== 0 && <Modal task={selectedTask} onClose={closeModal} />}
    </div>
  );
}
