import React, { useState } from "react";
import "./Tasks.css";

function TeamButton({
    number,
    selected,
    onTeamClick,
  }: {
    number: number;
    selected: boolean;
    onTeamClick: () => void;
  }) {

    return (
      <button
        className={`team ${selected ? "selected" : ""}`}
        onClick={onTeamClick}
      >
        <div className="team-container">
          <div className="team-content">
          </div>
        </div>
      </button>
    );
  }

