import React, { useState } from "react";
import "./Tasks.css";

function TaskButton({
  number,
  selected,
  onClick,
}: {
  number: number;
  selected: boolean;
  onClick: () => void;
}) {
  return (
    <button
      className={`task ${selected ? "selected" : ""}`}
      onClick={onClick}
    >
      <div className="task-container">
        <div className="task-content">
          {number}
        </div>
      </div>
    </button>
  );
}

export default function Tasks() {
  const [task, setTask] = useState(0);

  const onClick = (id: number) => {
    if (id === task) {
      setTask(0);
    } else {
      setTask(id);
      // Open a popup window with the task number displayed
      const popup = window.open("", "_blank", "width=200,height=100");
      if (popup) {
        popup.document.write(`<p>Task Number: ${id}</p>`);
        popup.document.close();
      }
    }
  };

  return (
    <div className="task-list">
      <TaskButton
        number={1}
        selected={task === 1}
        onClick={() => onClick(1)}
      />
      <TaskButton
        number={2}
        selected={task === 2}
        onClick={() => onClick(2)}
      />
      <TaskButton
        number={3}
        selected={task === 3}
        onClick={() => onClick(3)}
      />
      <TaskButton
        number={4}
        selected={task === 4}
        onClick={() => onClick(4)}
      />
      <TaskButton
        number={5}
        selected={task === 5}
        onClick={() => onClick(5)}
      />
      <TaskButton
        number={6}
        selected={task === 6}
        onClick={() => onClick(6)}
      />
    </div>
  );
}
