import axios from 'axios';

const API_URL = 'http://localhost:1337/task';

export const getTasks = async () => {
  try {
    const response = await axios.get(API_URL);
    return response.data;
  } catch (error) {
    console.error('Error fetching tasks:', error);
    throw error;
  }
};

export const getAvailableTasksForPerson = async (person: string) => {
  try {
    const response = await axios.get(API_URL, {
      params: {
        name: person

      }
    });
    return response.data;
  } catch (error) {
    console.error('Error fetching tasks:', error);
    throw error;
  }
};

export const getCompletedTasksForPerson = async (person: string) => {
  try {
    const response = await axios.get(API_URL, {
      params: {
        name: person
      }
    });
    return response.data;
  } catch (error) {
    console.error('Error fetching tasks:', error);
    throw error;
  }
};
