import "./Tasks.css";

function TeamButton({
    number,
    selected,
    onTeamClick,
  }: {
    number: number;
    selected: boolean;
    onTeamClick: () => void;
  }) {
    const [person, setPerson] = useState<String>("");

    const onClick = (name: String) => {
      if (name === person) {
        setPerson("");
      } else {
        setPerson(name);
      }
    };

    return (
      <button
        className={`team ${selected ? "selected" : ""}`}
        onClick={onTeamClick}
      >
        <div className="team-container">
          <div className="team-content">
            <div className="team-header">TEAM {number} TOTAL POINTS</div>
            <div className="points">
              <div className="current">1000</div>
              <div className="max">/ 9700</div>
            </div>
            <div className="members">
              <div className="members">
                {["Michael", "Kevin", "Bob", "Larissa", "Gabby"].map((name) => (
                  <PersonButton
                    key={name}
                    name={name}
                    selected={person === name}
                    onClick={() => onClick(name)}
                  />
                ))}
              </div>
            </div>
          </div>
        </div>
      </button>
    );
  }

export default function Tasks() {

  return (
    <div className="team-list">
      <TaskButton
        number={1}
        selected={team === 1 || team === 0}
        onTeamClick={() => onTeamClick(1)}
      />
      <TaskButton
        number={2}
        selected={team === 2 || team === 0}
        onTeamClick={() => onTeamClick(2)}
      />
      <TaskButton
        number={3}
        selected={team === 3 || team === 0}
        onTeamClick={() => onTeamClick(3)}
      />
    </div>
  );
}
