import React, { useState } from "react";
import "./Teams.css";

function TeamButton({
    number,
    selected,
    onClick,
}: {
    number: number;
    selected: boolean;
    onClick: () => void;
}){
    return (
        <button
          className={`person ${selected ? 'selected' : ''}`}
          onClick={onClick}
        >
          {name}
        </button>
    );
}

function PersonButton({
    name,
    selected,
    onTeamClick,
  }: {
    name: String;
    selected: boolean;
    onTeamClick: () => void;
  }) {

    const [person, setPerson] = useState<String>('');

    const onClick = (name: String) => {
        if(name === person) {
            setPerson('');
        } else {
            setPerson(name);
        }

    }

    return (
      <button
        className={`team-container ${selected ? 'selected' : ''}`}
        onClick={onTeamClick}
      >
        <div className="team-content">
          <div className="team-header">TEAM 1 TOTAL POINTS</div>
          <div className="points">
            <div className="current">1000</div>
            <div className="max">/ 9700</div>
          </div>
          <div className="members">
          <div className="members">
              {["Michael", "Kevin", "Bob", "Larissa", "Gabby"].map((name) => (
                <PersonButton
                  key={name}
                  name={name}
                  selected={person === name}
                  onClick={() => onClick(name)}
                />
              ))}
            </div>
          </div>
        </div>
      </button>
    );
  }

export default function Teams() {
    const [team, setTeam] = useState('');

  return (
    <div className="team-list">
      <TeamButton number={1} />
      <div className="team-container">
        <div className="team-content">
          <div className="team-header">TEAM 2 TOTAL POINTS</div>
          <div className="points">
            <div className="current">1000</div>
            <div className="max">/ 9700</div>
          </div>
          <div className="members">
          <div className="members">
              {["Michael", "Kevin", "Bob", "Larissa", "Gabby"].map((name) => (
                <PersonButton
                  key={name}
                  name={name}
                  selected={person === name}
                  onClick={() => onClick(name)}
                />
              ))}
            </div>
          </div>
        </div>
      </div>
      <div className="team-container">
        <div className="team-content">
          <div className="team-header">TEAM 3 TOTAL POINTS</div>
          <div className="points">
            <div className="current">1000</div>
            <div className="max">/ 9700</div>
          </div>
          <div className="members">
          <div className="members">
              {["Michael", "Kevin", "Bob", "Larissa", "Gabby"].map((name) => (
                <PersonButton
                  key={name}
                  name={name}
                  selected={person === name}
                  onClick={() => onClick(name)}
                />
              ))}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
